<?phpinclude '../database/db_config.php';session_start();if ($_SESSION['adminview']){	$conn = mysql_connect($dbhost, $dbuser, $dbpass);	mysql_select_db($dbname, $conn);		if(isset($_GET['action'])&&isset($_GET['album_id']))	{		$action = $_GET['action'];		$album_id = $_GET['album_id'];		if($_GET['action'] == 'delete')		{			$query =   "DELETE 						FROM $albumtable						WHERE id = '$album_id'";			mysql_query($query);			header("Location: ../main.php?l=soc_albums");		}		if($_GET['action'] == 'edit')		{			$query =   "SELECT * 						FROM $albumtable						WHERE id = '$album_id'";			$result = mysql_query($query);			$row = mysql_fetch_array($result);			$name = $row['name'];			$description = $row['description'];			$time = $row['time'];			$place = $row['place'];						?>			<form action="edit.php?l=check_album_form" method="POST">			Album name:  <input type="text" id="name" name="name" value="<? if(isset($name)) echo $name; ?>"> <br /><br />			Description:  <input type="text" id="description" name="description" value="<? if(isset($description)) echo $description; ?>"> <br /><br />			Date:  <input type="text" id="time" name="time" value="<? if(isset($time)) echo $time; ?>"> <br /><br />			Place: <input type="text" id="place" name="place" value="<? if(isset($place)) echo $place; ?>"> <br /><br />			<input type="submit" value="Submit"><br /><br />			</form>			<?			//header("Location: ../main.php?l=soc_albums");		}					}	else	{		header("Location: main.php");	}}else header("Location: access-denied.php");?>