<?php 	include '../database/db_config.php';		// get society_id	$query = "SELECT society_id			  FROM $usertable			  WHERE username = '".$_SESSION['username']."';";	$result = mysql_query($query); 	$result_arr = mysql_fetch_array($result);	if (empty($result_arr)) {$society_id = 0; $society_name = 'NO SOCIETY';}	else	{		$society_id = $result_arr[0];			//get society_name		$query = "SELECT name				  FROM $societytable				  WHERE id = '$society_id';";		$result = mysql_query($query);		$result_arr = mysql_fetch_array($result);			if (empty ($result_arr)) $society_name = '';		else $society_name = $result_arr[0];	}?><p> You are an admin of the society: <? echo $society_name; ?> and this is your home page. </p><p> You have the rights to modify everything !! </p><div id="option1">    <a href="../main.php?l=socpage">         Edit the homepage of <? echo $society_name; ?>     </a></div>