<?php     global $path;	include $path.'database/db_config.php'; // path from index.php		// get society_id	$query = "SELECT society_id			  FROM $usertable			  WHERE username = '".$_SESSION['username']."';";	$result = mysql_query($query); 	$result_arr = mysql_fetch_array($result);    	if (empty($result_arr)) {$society_id = 0; $society_name = 'NO SOCIETY';}	else	{		$society_id = $result_arr[0];			//get society_name		$query = "SELECT name				  FROM $societytable				  WHERE id = '$society_id';";		$result = mysql_query($query);		$result_arr = mysql_fetch_array($result);			if (empty ($result_arr)) $society_name = '';		else $society_name = $result_arr[0];	}?><p>     You are an admin of the society: <? echo $society_name; ?> and this is your home page.     You have the rights to modify <? echo 'dadada'?>.</p><div id="options">    <div class="options_row">        <div class="options_cell">            <a href="../index.php?l=soc_page">                 Edit the Homepage of <? echo $society_name; ?>             </a>        </div>        <div class="options_cell">            <a href="../index.php?l=soc_events">                 Edit the Events of <? echo $society_name; ?>             </a>        </div>    </div>    <div class="options_row">        <div class="options_cell">            <a href="../index.php?l=soc_albums">                 Edit the Photos of <? echo $society_name; ?>             </a>        </div>        <div class="options_cell">            <a href="../index.php?l=soc_blog">                 Edit the Blogs of <? echo $society_name; ?>             </a>        </div>    </div>    <div class="options_row">        <div class="options_cell">            <a href="../index.php?l=soc_members">                 Edit the Members of <? echo $society_name; ?>             </a>        </div>        <div class="options_cell">            <a href="../index.php?l=soc_settings">                 Edit the Details of <? ech