<?php    $inputText = isset($_GET['q']) ? $_GET['q'] : 'Find...';        // userBox     if (!isLoggedIn())     {         function echoUserMenu() { }        function showUserText()         {             echo 'Hello guest!';            <a href="<? echo $path; ?>login/logout.php">Sign out</a>        }     }    else     { // is logged in        function echoUserMenu()         {            global $path;            ?>            <div id="userMenu"><div>                <a href="options.php">Options</a>                <a href="<? echo $path; ?>login/logout.php">Sign out</a>            </div></div>            <?php        }        function showUserText()         {            global $username, $isAdmin, $adminView, $path;            ?>            Hello, <? echo $username; ?>            <? if ($isAdmin && $adminView)             { ?>                echo '<br /><a href="'.$path.'user/">Go to user view</a>';            }            else if ($isAdmin)            {                echo '<br /><a href="'.$path.'admin/">Go to admin view</a>';            }            <?php        }                    }    // end of userBox     function showHeader(){    global $tab1_link, $tab1_title, $tab2_link, $tab2_title, $inputText;    ?>    <div id="home">        <? include 'menu.php'; ?>        <a href="<? echo $tab1_link; ?>" tabindex="10">              <? echo $tab1_title; ?>        </a>    </div>    <div id="pers">        <? include 'books.php'; ?>        <a href="<? echo $tab2_link; ?>" tabindex="11">            <? echo $tab2_title; ?>        </a>    </div>    <div id="find" onclick="document.getElementById(\'q\').focus();">        <form action="<? echo htmlentities($_SERVER['PHP_SELF']); ?>" method="get">            <div>                <input id="q" name="q" type="text" value="<? echo $inputText; ?>"                     tabindex="12" onfocus="qFocus();" onblur="qBlur();" />                <div id="reset" onclick="qReset();" value="X"></div>                <button id="search" type="submit" value="GO" tabindex="13"></button>            </div>        </form>    </div>        <div id="userBox">        <? echoUserMenu(); ?>        <div>            <? showUserText(); ?>        </div>    </div><? } ?>