<?php    $menuLink = $_SESSION['adminview']?'admin/menu.php':'user/menu.php';    $bookLink = $_SESSION['adminview']?'admin/books.php':'user/books.php';    $inputText = isset($_GET['q'])?$_GET['q']:'Find...';        // userBox     if (!isLoggedIn())     {         $userBoxText = 'Please <a href="login/login.php">login</a>';    }    else     { // is logged in        $username = $_SESSION['username'];        $query = "SELECT membership_status_id                  FROM $usertable                  WHERE username = '$username';";        $result = mysql_query($query);        $result_arr = mysql_fetch_array($result);        $isAdmin = $result_arr[0] == $mem_status_admin;                if ($isAdmin && isset($_GET['a']))  // change view        {            if ($_GET['a'] == 0) { $_SESSION['adminview']=false; }            else if ($_GET['a'] == 1) { $_SESSION['adminview']=true; }        }                $userBoxText = 'Hello, '.$_SESSION['username'].'!';                if ($isAdmin)         {            if ($_SESSION['adminview'])  // admin view            {                $switchViewText = '<a href="index.php?a=0">go to user view</a>';            }            else               // user view            {                $switchViewText = '<a href="index.php?a=1">go to admin view</a>';            }        }        $logoutText = '<a href="login/logout.php">Logout</a>';    }    // end of userBox     function showHeader(){    global $tab1_link, $tab1_title, $menuLink;    global $tab2_link, $tab2_title;     global $bookLink, $tab3_link, $inputText;    global $userBoxText, $switchViewText, $logoutText;    ?>    <div id="home">        <? include $menuLink; ?>        <a href="<? echo $tab1_link; ?>" tabindex="10">              <? echo $tab1_title; ?>        </a>    </div>    <div id="pers">        <? include $bookLink; ?>        <a href="<? echo $tab2_link; ?>" tabindex="11">            <? echo $tab2_title; ?>        </a>    </div>    <div id="find" onclick="document.getElementById(\'q\').focus();">        <form action="<? echo $tab3_link; ?>" method="get">            <div>                <input id="q" name="q" type="text" value="                    <? echo $inputText; ?>                    " tabindex="12" onfocus="qFocus();" onblur="qBlur();" />                <div id="reset" onclick="qReset();" value="X"></div>                <button id="search" type="submit" value="GO" tabindex="13"></button>            </div>        </form>    </div>        <div id="userBox">        <div>            <? echo $userBoxText;                echo $switchViewText;                echo $logoutText; ?>        </div>    </div><? } ?>