<?php    if (!isLoggedIn())     {         $userBoxText = 'Please <a href="login/login.php">login</a>';    }    else     { // is logged in        $username = $_SESSION['username'];        $query = "SELECT membership_status_id                  FROM $usertable                  WHERE username = '$username';";        $result = mysql_query($query);        $result_arr = mysql_fetch_array($result);        $isAdmin = $result_arr[0] == $mem_status_admin;                if ($isAdmin && isset($_GET['a']))  // change view        {            if ($_GET['a'] == 0) { $_SESSION['adminview']=false; }            else if ($_GET['a'] == 1) { $_SESSION['adminview']=true; }        }                $userBoxText = 'Hello, '.$_SESSION['username'].'!';                if ($isAdmin)         {            if ($_SESSION['adminview'])  // admin view            {                $switchViewText = '<a href="index.php?a=0">go to user view</a>';            }            else               // user view            {                $switchViewText = '<a href="index.php?a=1">go to admin view</a>';            }        }        $logoutText = '<a href="login/logout.php">Logout</a>';    }function showHeader(){    global $tab1_link, $tab1_title;    echo '<div id="home">';    if ($_SESSION['adminview']) { include 'admin/menu.php'; }     else { include 'user/menu.php'; }    echo ' <a href="';    echo $tab1_link;    echo '" tabindex="10">  ';    echo $tab1_title;    echo '</a>';    echo '</div>';    global $tab2_link, $tab2_title;    echo '<div id="pers">';    if ($_SESSION['adminview']) { include 'admin/books.php'; }    else { include 'user/books.php'; }     echo '<a href="';    echo $tab2_link;    echo '" tabindex="11">';    echo $tab2_title;    echo '</a>';    echo '</div>';    global $tab3_link;    echo '<div id="find" onclick="document.getElementById(\'q\').focus();">';    echo '<form action="';    echo $tab3_link;     echo '" method="get">';    echo '<div>';    echo '<input id="q" name="q" type="text" value="';    echo isset($_GET['q'])?$_GET['q']:'Find...';     echo '" tabindex="12" onfocus="qFocus();" onblur="qBlur();" />';    echo '<div id="reset" onclick="qReset();" value="X"></div>';    echo '<button id="search" type="submit" value="GO" tabindex="13"></button>';    echo '</div>';    echo '</form>';    echo '</div>';    global $userBoxText, $switchViewText, $logoutText;    echo '<div id="userBox">';    echo '<div>';    echo $userBoxText;    echo $switchViewText;    echo $logoutText;    echo '</div>';    echo '</div>';}?>